import { Component, ɵisListLikeIterable } from '@angular/core';
import { WebcamImage, WebcamMirrorProperties } from 'ngx-webcam/public_api';
import { Observable, Subject } from 'rxjs';


@Component({
  selector: 'app-formpage',
  templateUrl: './formpage.component.html',
  styleUrls: ['./formpage.component.css']
})
export class FormpageComponent {
  stream:any = null;
  trigger: Subject<void> = new Subject();
  // savedTriggers:Subject<void>[] = [];
  previewImage: string = '';
  savedImages:string[] = [];  
  btnLabel: string = 'Take a Pic' 
  status!: string;
  isShowing:boolean=false;
  camIsStreaming: boolean=false;
  displayCamera: boolean= false;
  displayDescription: boolean= false;
  formContainer: boolean= false;


get $trigger(): Observable<void> {
    return this.trigger.asObservable(); 
}

snapshot(event: WebcamImage) {
  console.log(event); 
  this.previewImage = event.imageAsDataUrl;
  this.btnLabel = 'Re-take Pic'

  //save in array
  const imageFromEvent = event.imageAsDataUrl;
  this.savedImages.push(imageFromEvent);

  console.log("thia is the list", this.savedImages);
}

  

  // create function that reads the src
  // compares it to the l i s that
  // removes it if there

  removeImage(image:string) {
    const indexOfImage = this.savedImages.indexOf(image);

    if(indexOfImage != -1) {
      this.savedImages.splice(indexOfImage,1);
    }
    // if (this.savedImages.includes(image)){
    //   console.log("we inside")
    //   console.log(this.savedImages);
    //   console.log(this.savedImages.filter((img) => img == image));
    //   this.savedImages = this.savedImages.filter((img) => img == image);
    //   console.log(this.savedImages);
    // }

  }

  getPermission() {
    navigator.mediaDevices.getUserMedia({
      video:{
        width: 500,
        height: 500
      }
    }).then((res) => {
      console.log("response", res);
      this.stream = res;
      this.status = 'My camera is being accessed';
      this.btnLabel = 'Take a Pic';
      if (!this.isShowing) {
        this.isShowing=true;
      }
    }).catch(err =>{
      console.log(err);
      if(err?.message === 'Permission denied') {
        this.status = 'Permission denied please try again by approving webcam access';
      } else {
          this.status = 'You may not have a webcam connected to your system. Please try again....';
        }
    })
  }

  captureImage(){
     this.trigger.next();   
  }

sumbit(){
  console.log(this.previewImage);
}






}





<!-- <button><img src="../../assets/images/"></button> -->

<div class="imageBox">
  <!-- Check Permission/open Camera -->
  <!-- <button (click)="getCamData()">Open Camera</button> -->
 <div *ngIf="!isShowing" class="imageSection"> 
  <button (click)="getPermission()">
    <div >
      <img src="../../assets/images/photo_camera_FILL1_wght300_GRAD200_opsz48.svg" alt="">
    </div>

    <div *ngIf="isShowing">
      <img src="../../assets/images/add_a_photo_FILL1_wght300_GRAD200_opsz48.svg" alt="">
    </div>

  </button>
</div>

<div *ngIf="isShowing" class="imageSection red">
  <webcam  [height]="400" [width]="400" [trigger]="$trigger" (imageCapture)="snapshot($event)" [imageQuality]="1"></webcam> 
  <button (click)="captureImage()">{{btnLabel}}</button>  
</div>

<div *ngIf="!previewImage"  class="instructionContainer">
        Take Some Photos 
      of the Issue
      Click on the camera icon to get started
</div>



<div class="formContainer">
  <div *ngIf="previewImage">

    <div class="screenshots">
      <div  *ngFor='let image of savedImages'>
        <img [src]="image" height="90" width="150" (click)="removeImage(image)" >
      </div>
  
    </div>
  
  </div>
  
  
  <h2>Description</h2>
          
  <div class="textarea">
  <textarea name="Description" id="Descriptionbox" cols="35" rows="6" placeholder="Describe the issue here"></textarea>
  </div>
  
  
  <div class="bottombuttons">
  <button>Back</button><button>Submit</button>
  </div>
  
</div>
  <!-- Preview Section -->
