import { Component } from '@angular/core';
import { WebcamImage } from 'ngx-webcam/public_api';
import { Subject } from 'rxjs';


@Component({
  selector: 'app-formpage',
  templateUrl: './formpage.component.html',
  styleUrls: ['./formpage.component.css']
})
export class FormpageComponent {
  stream:any = null;
  trigger: Subject<void> = new Subject();
  previewImage: string = ''; 

get $trigger(): Observable<void> {
    return this.trigger.asObservable(); 
}

snapshot(event: WebcamImage) {
  console.log(event); 
  this.previewImage = event.imageAsDataUrl;
}

  isShowing:boolean=false;
  showVideo() {
    this.isShowing=!this.isShowing;
  }
  checkPermissions() {
    navigator.mediaDevices.getUserMedia({
      video:{
        width: 500,
        height: 500
      }
    }).then((res) => {
      console.log("response", res);
      this.stream = res;
      if (!this.isShowing) {
        this.isShowing=true;
      }
    }).catch(err =>{
      console.log (err);
    })
  }

  captureImage(){
     this.trigger.next();   
  }

}





<div>
  <button (click)="checkPermissions()">Check the webcam persmissions</button>
  
</div>
<div>
  <!-- webcame image will be shown here -->
<webcam *ngIf="isShowing" [trigger]="$trigger" (imageCapture)="snapshot($event)"></webcam> 
<button (click)="captureImage()">Capture </button>
<!-- or -->
  <!-- <button *ngIf="isShowing"><webcam ></webcam></button> -->   

</div>


  <!-- Preview Section -->
  <div *ngIf="previewImage">
  <img [src]="previewImage" height="200" width="200">
</div>


<h2>Description</h2>
        
<div class="textarea">
<textarea name="Description" id="Descriptionbox" cols="35" rows="6" placeholder="Describe the issue here"></textarea>
</div>


<div class="bottombuttons">
<button>Back</button><button>Submit</button></div>
