import { Component } from '@angular/core';


@Component({
  selector: 'app-formpage',
  templateUrl: './formpage.component.html',
  styleUrls: ['./formpage.component.css']
})
export class FormpageComponent {

}
const input = document.querySelector('input');
const preview = document.querySelector('.preview');

input.style.opacity = 0;
input.addEventListener('change', updateImageDisplay);

function updateImageDisplay() {
  while(preview.firstChild) {
    preview.removeChild(preview.firstChild);
  }

  const curFiles = input.files;
  if (curFiles.length === 0) {
    const para = document.createElement('p');
    para.textContent = 'No files currently selected for upload';
    preview.appendChild(para);
  } else {
    const list = document.createElement('ol');
    preview.appendChild(list);

    for (const file of curFiles) {
      const listItem = document.createElement('li');
      const para = document.createElement('p');
      if (validFileType(file)) {
        para.textContent = `File name ${file.name}, file size ${returnFileSize(file.size)}.`;
        const image = document.createElement('img');
        image.src = URL.createObjectURL(file);

        listItem.appendChild(image);
        listItem.appendChild(para);
      } else {
        para.textContent = `File name ${file.name}: Not a valid file type. Update your selection.`;
        listItem.appendChild(para);
      }

      list.appendChild(listItem);
    }
  }
};

<h2>Photos</h2> 

<form method="post" enctype="multipart/form-data"> 
<div> 
    <!-- <label for="openCamera">Takes a Photo</label>  -->

<input  
type="file"  
id="openCamera"  
accept="image/*"  
style="display: none;"
name="file" multiple/> 
</div> 
<label for="openCamera" style="cursor: pointer;">Add A Photo</label>


<p><img id="output" width="50"/></p>


<h2>Description</h2>
        
<div class="textarea">
<textarea name="Description" id="Descriptionbox" cols="35" rows="6" placeholder="Describe the issue here"></textarea>
</div>


<div class="bottombuttons">
<button>Back</button><button>Submit</button></div>
