import { Component } from '@angular/core';

import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {



  firstName: string ="";
  lastName: string ="";
  userName: string ="";

  email: string = '';
  password: string = '';

  isSignup: boolean = false;
  erroMessage: string = "";


  constructor(private router: Router,private http: HttpClient) {}

  signup() {
    console.log(this.email);
    console.log(this.password);

    let bodyData = {
     
      firstName: this.firstName,
      lastName:this.lastName,
      userName: this.userName,
      email: this.email, 
      password: this.password,
      isSignup:this.isSignup
    };

        this.http.post("http://localhost:4200/", bodyData).subscribe(  (resultData: any) => {
        console.log(resultData);

        if (resultData.status) 
        {
      
           this.router.navigateByUrl('/home');
    

        } 
        else
         {
          alert(" Email or Password required");
          console.log("Errror signup");
        }
      });
    }

}

<form >
  <div>
    <h1>Sign up</h1>
  </div>

  <div>
    <label for="fname"> </label><br />
    <input class="textArea" type="text" [(ngModel)]="firstName" id="fname" name="firstName" placeholder="First Name" /><br />
  </div>
  <div>
    <label for="Lname"> </label><br />
    <input class="textArea" type="text" [(ngModel)]="lastName" id="Lname" name="lastName" placeholder="Last Name"/><br />
  </div>
  <div>
    <label for="username"></label><br />
    <input
      type="text"
      [(ngModel)]="userName"
      id="Uname"
      name="userName"
      placeholder="United ID"
      class="textArea"
    /><br />
  </div>
  <div>
    <label for="uemail"></label><br />
    <input type="text" [(ngModel)]="email" id="u-email" name="email" placeholder="United Email" class="textArea"/><br />
  </div>
  <div>
    <label for="password"></label><br />
    <input
      type="text"
      [(ngModel)]="password"
      id="password"
      name="password"
      placeholder="Create Password"
      class="textArea"
    /><br />
  </div>
  <div id="input-container">
    <button type="submit" id="submit-btn" name="submit" value="Sign up" >Sign Up</button>
  </div>
  <div class="link">
    <a routerLink="/">Log In</a>
  </div>
</form>
