import { Component } from '@angular/core';


@Component({
  selector: 'app-formpage',
  templateUrl: './formpage.component.html',
  styleUrls: ['./formpage.component.css']
})
export class FormpageComponent {

}
const input = document.querySelector('input');
const preview = document.querySelector('.preview');





<h2>Photo</h2> 

<!-- camera logo pic -->
<script src="https://use.fontawesome.com/3a2eaf6206.js"></script>

<!-- form/div/label for camera "button" -->
<form method="post" enctype="multipart/form-data"> 
<div class="camera"> 


  <label for="openCamera">Take a Photo
<br>
    <!-- idk? -->
    <i class="fa fa-2x fa-camera"></i>

    <!-- open camer/choose photo from library -->
<input  
type="file"  
id="openCamera"  
accept="image/*"  
style="display: none;"
onchange="loadFile(event)" 
name="file" multiple/> 

<!-- idk? -->
<span id="imageName"></span>

</label>
</div> 

<!-- photo preview dimesions -->
<p><img id="output" width="50"/></p>

<!-- photo preview -->
<script> 

  var loadFile = function(event) { 
    var image = document.getElementById('output'); 
    image.src = URL.createObjectURL(event.target.files[0]); 
  }; 
  </script> 

<h2>Description</h2>
        
<div class="textarea">
<textarea name="Description" id="Descriptionbox" cols="35" rows="6" placeholder="Describe the issue here"></textarea>
</div>


<div class="bottombuttons">
<button>Back</button><button>Submit</button></div>



<script>
  let input = document.getElementById("inputTag");
  let imageName = document.getElementById("imageName")

  input.addEventListener("change", ()=>{
      let inputImage = document.querySelector("input[type=file]").files[0];

      imageName.innerText = inputImage.name;
  })
</script>
