import { Component, NgModule } from '@angular/core';

import {WebcamModule,WebcamImage} from 'ngx-webcam';
import { Observable, Subject } from 'rxjs';

  
export class AppModule { }

@Component({
  selector: 'app-formpage',
  templateUrl: './formpage.component.html',
  styleUrls: ['./formpage.component.css']
})
export class FormpageComponent {
  stream:any = null;
  trigger: Subject<void> = new Subject();
  // savedTriggers:Subject<void>[] = [];
  previewImage: string = '';
  savedImages:string[] = [];  
  btnLabel: string = 'Take a Pic' 
  status!: string;
  camIsShowing:boolean=false;
  camIsStreaming: boolean=false;
  displayCamera: boolean= false;
  displayDescription: boolean= false;
  formContainer: boolean= false;
  imageBox: boolean=true;
  imageBoxVisible: boolean =true;
  formInitialized: boolean=true;
  reopenCameraBtn: boolean=false;
  isFlash: boolean=false;
  

get $trigger(): Observable<void> {
    return this.trigger.asObservable(); 
}

snapshot(event: WebcamImage) {
  // console.log(event); 
  this.previewImage = event.imageAsDataUrl;


  //save in array
  const imageFromEvent = event.imageAsDataUrl;
  this.savedImages.push(imageFromEvent);
}

toggleReopenCam() {
  this.reopenCameraBtn=!this.reopenCameraBtn;
}
  // create function that reads the src
  // compares it to the l i s that
  // removes it if there

  removeImage(image:string) {
    const indexOfImage = this.savedImages.indexOf(image);

    if(indexOfImage != -1) {
      this.savedImages.splice(indexOfImage,1);
    }
  }

  getPermission() {
    navigator.mediaDevices.getUserMedia({
      video:{
        width: 500,
        height: 500
      }
    }).then((res) => {
      // console.log("response", res);
      this.stream = res;
      this.status = 'My camera is being accessed';
      this.btnLabel = 'Take a Pic';
      if (!this.camIsShowing) {
        this.camIsShowing=true;
      }
    }).catch(err =>{
      // console.log(err);
      if(err?.message === 'Permission denied') {
        this.status = 'Permission denied please try again by approving webcam access';
      } else {
          this.status = 'You may not have a webcam connected to your system. Please try again....';
        }
    })
  }

  captureImage(){
    console.log("cat palace")

    this.isFlash=true;

    setTimeout(() => {
      console.log("dawg castle");
      this.isFlash=false;

    }, 200);
    

     this.trigger.next();   
     if (this.formInitialized==true){
      this.formInitialized=false;
     }
  }

sumbit(){
  console.log(this.previewImage);
}

cameraOff() {
  this.imageBoxVisible=false;
  this.toggleReopenCam();
}

reopenCam() {
    this.imageBoxVisible=true;
      this.camIsShowing= true;
    this.toggleReopenCam();
}
}


<div class="formpage-body">
  <div class="formpage-container">
    <div class="header">
      <button class="closeButton">
        <img
          class="closeImage"
          src="../../assets/images/close-icon.svg"
          alt="Close Window"
        />
      </button>
    </div>

    <div
      class="imageBox"
      [ngClass]="{ flash: isFlash }"
      *ngIf="imageBoxVisible"
    >
      <div class="logo">
        <img
          src="../../assets/images/photo_camera_FILL1_wght300_GRAD200_opsz48.svg"
          alt=""
        />
      </div>
      <div class="imageSection cam-permission" *ngIf="!camIsShowing">
        <button class="cameraImage" (click)="getPermission()"></button>
      </div>

      <button
        (click)="captureImage()"
        *ngIf="camIsShowing"
        class="imageSection red"
      >
        <webcam
          [height]="900"
          [width]="800"
          [trigger]="$trigger"
          (imageCapture)="snapshot($event)"
          [imageQuality]="1"
        ></webcam>
        <div class="takePic">
          <img
            src="../../assets/images/photo_camera_FILL1_wght300_GRAD200_opsz48.svg"
            alt=""
          />
        </div>
      </button>
    </div>

    <div class="instructionContainer" *ngIf="!previewImage">
      <h1>Take Some Photos<br />of the Issue</h1>
      <p>Click on the camera icon to get<br />started</p>
    </div>

    <div class="formContainer">
      <div *ngIf="previewImage">
        <div class="screenshots">
          <div class="screenshotText" *ngIf="!reopenCameraBtn">
            Swipe up on photo to remove
          </div>
          <button
            class="innerDivCameraBtn"
            *ngIf="reopenCameraBtn"
            (click)="reopenCam()"
          >
            <div class="innerDivCircle">
              <img
                class="innerDivCameraPic"
                src="../../assets/images/add_a_photo_FILL1_wght300_GRAD200_opsz48.svg"
                alt=""
              />
            </div>
          </button>
          <div class="innerDiv-container">
            <div class="innerDiv" *ngFor="let image of savedImages">
              <div class="preview-Image-container">
                <img
                  [src]="image"
                  height="150"
                  width="150"
                  (click)="removeImage(image)"
                />
              </div>
            </div>
          </div>
        </div>
      </div>

      <div
        [hidden]="(camIsShowing && !previewImage) || formInitialized"
        class="textarea"
      >
        <h2>Description</h2>
        <div>
          <textarea
            (click)="cameraOff()"
            name="Description"
            id="Descriptionbox"
            cols="35"
            rows="10"
            placeholder="Describe the issue here"
          ></textarea>
        </div>
      </div>

      <div class="submitBtnDiv" [hidden]="!previewImage">
        <button class="submitBtn">Submit</button>
      </div>
    </div>
  </div>
</div>
