import { Component } from '@angular/core';

import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {



  firstName: string ="";
  lastName: string ="";
  userName: string ="";

  email: string = '';
  password: string = '';

  isSignup: boolean = false;
  erroMessage: string = "";


  constructor(private router: Router,private http: HttpClient) {}

  signup() {
    console.log(this.email);
    console.log(this.password);

    let bodyData = {
     
      firstName: this.firstName,
      lastName:this.lastName,
      userName: this.userName,
      email: this.email, 
      password: this.password,
      isSignup:this.isSignup
    };

        this.http.post("http://localhost:4200/", bodyData).subscribe(  (resultData: any) => {
        console.log(resultData);

        if (resultData.status) 
        {
      
           this.router.navigateByUrl('/home');
    

        } 
        else
         {
          alert(" Email or Password required");
          console.log("Errror signup");
        }
      });
    }

}

<form >
  <div>
    <h1>Sign up</h1>
  </div>

  <div>
    <label for="fname">First name:</label><br />
    <input type="text" [(ngModel)]="firstName" id="fname" name="firstName" /><br />
  </div>
  <div>
    <label for="Lname">Last name:</label><br />
    <input type="text" [(ngModel)]="lastName" id="Lname" name="lastName" /><br />
  </div>
  <div>
    <label for="username">User name:</label><br />
    <input
      type="text"
      [(ngModel)]="userName"
      id="Uname"
      name="userName"
    /><br />
  </div>
  <div>
    <label for="uemail">United Email:</label><br />
    <input type="text" [(ngModel)]="email" id="u-email" name="email" /><br />
  </div>
  <div>
    <label for="password">Password:</label><br />
    <input
      type="text"
      [(ngModel)]="password"
      id="password"
      name="password"
    /><br />
  </div>
  <div id="input-container">
    <input type="submit" id="submit-btn" name="submit" value="Sign up" />
  </div>
  <div class="link">
    <a routerLink="/">Log In</a>
  </div>
</form>
